// frontend/src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:3001'; // URL untuk Transaction Service

export const sendMoney = async (amount) => {
  const response = await axios.post(`${API_URL}/send`, { amount });
  return response.data;
};

export const withdrawMoney = async (amount) => {
  const response = await axios.post(`${API_URL}/withdraw`, { amount });
  return response.data;
};

export const getAccounts = async () => {
  const response = await axios.get(`${API_URL}/accounts`);
  return response.data;
};

export const getTransactions = async () => {
  const response = await axios.get(`${API_URL}/transactions`);
  return response.data;
};

export const processTransaction = async (transaction) => {
  // Simulasi pemrosesan transaksi, sesuaikan dengan implementasi Anda
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve(transaction);
    }, 3000); // 3 detik
  });
};
